import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddressInput = () => {
  const [address, setAddress] = useState('');
  const navigate = useNavigate();

  const handleAddressSearch = () => {
    new window.daum.Postcode({
      oncomplete: function (data) {
        setAddress(data.address);
      },
    }).open();
  };

  const handleNext = () => {
    navigate('/register-villa/parking', { state: { address } });
  };

  return (
    <div>
      <h2>내 빌라 등록 - 주소 입력</h2>
      <div>
        <label>Address:</label>
        <input type="text" value={address} readOnly required />
        <button type="button" onClick={handleAddressSearch}>Search Address</button>
      </div>
      <button type="button" onClick={handleNext} disabled={!address}>Next</button>
    </div>
  );
};

export default AddressInput;
