import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/VillaList.css';

const VillaList = () => {
  const [villas, setVillas] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchVillas = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/villa/all');
        setVillas(response.data);
      } catch (error) {
        console.error('Error fetching villas:', error);
      }
    };

    fetchVillas();
  }, []);

  const handleDetailClick = (villaId) => {
    navigate(`/villa/${villaId}`);
  };

  const handleUserListClick = (villaId) => {
    navigate(`/villa/${villaId}/users`);
  };

  return (
    <div className="villa-list-container">
      <h2>빌라 목록</h2>
      <ul className="villa-list">
        {villas.map((villa) => (
          <li key={villa._id}>
            <span>{villa.villaName} - {villa.address}</span>
            <button onClick={() => handleDetailClick(villa._id)}>상세보기</button>
            <button onClick={() => handleUserListClick(villa._id)}>유저 목록 보기</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default VillaList;
