import React, { useState } from 'react';
import axios from 'axios';

const ForgotCredentials = () => {
  const [phone, setPhone] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [isVerified, setIsVerified] = useState(false);
  const [message, setMessage] = useState('');

  const handleSendCode = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/api/user/send-code', { phone });
      setMessage(response.data.message);
    } catch (error) {
      if (error.response && error.response.data) {
        setMessage(error.response.data.message);
      } else {
        setMessage('Unexpected error occurred. Please try again.');
      }
    }
  };

  const handleVerifyCode = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/api/user/verify-code', { phone, code: verificationCode });
      if (response.data.verified) {
        setIsVerified(true);
        setMessage('Phone number verified! Please check your email for further instructions.');
        // 여기에서 이메일을 통한 추가 안내를 처리합니다.
      } else {
        setMessage('Invalid verification code. Please try again.');
      }
    } catch (error) {
      if (error.response && error.response.data) {
        setMessage(error.response.data.message);
      } else {
        setMessage('Unexpected error occurred. Please try again.');
      }
    }
  };

  return (
    <div>
      <h2>Forgot Credentials</h2>
      {!isVerified ? (
        <form onSubmit={handleSendCode}>
          <div>
            <label>Phone:</label>
            <input
              type="text"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
            />
          </div>
          <button type="submit">Send Verification Code</button>
          {message && <p>{message}</p>}
        </form>
      ) : (
        <form onSubmit={handleVerifyCode}>
          <div>
            <label>Verification Code:</label>
            <input
              type="text"
              value={verificationCode}
              onChange={(e) => setVerificationCode(e.target.value)}
              required
            />
          </div>
          <button type="submit">Verify Code</button>
          {message && <p>{message}</p>}
        </form>
      )}
    </div>
  );
};

export default ForgotCredentials;
