import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import '../styles/Login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // 컴포넌트가 마운트될 때 세션을 확인합니다.
    const checkSession = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/user/check-session', { withCredentials: true });
        if (response.data.sessionActive) {
          setMessage('이미 로그인된 상태입니다.');
          if (response.data.user.isAdmin) {
            navigate('/admin');
          } else {
            navigate('/main');
          }
        }
      } catch (error) {
        console.error('세션 확인 중 오류 발생:', error);
      }
    };
    checkSession();
  }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:3001/login', { username, password }, { withCredentials: true });
      if (response && response.data) {
        setMessage(response.data.message);
        localStorage.setItem('token', response.data.token);

        if (response.data.isAdmin) {
          navigate('/admin');
        } else {
          navigate('/main');
        }
      } else {
        setMessage('예기치 않은 오류가 발생했습니다. 다시 시도해 주세요.');
      }
    } catch (error) {
      if (error.response && error.response.data) {
        setMessage(error.response.data.message);
      } else {
        setMessage('예기치 않은 오류가 발생했습니다. 다시 시도해 주세요.');
      }
    }
  };

  return (
    <div className="login-container">
      <h2>로그인</h2>
      <form onSubmit={handleLogin}>
        <div className="input-group">
          <label>아이디:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="input-field"
          />
        </div>
        <div className="input-group">
          <label>비밀번호:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="input-field"
          />
        </div>
        <div className="button-group">
          <button type="submit" className="btn login-btn">로그인</button>
          <Link to="/register" className='link'>회원가입</Link>
        </div>
      </form>
      {message && <p className="message">{message}</p>}
      <div className="link-group">
        <Link to="/find-username" className="link">아이디 찾기</Link> | <Link to="/find-password" className="link">비밀번호 찾기</Link>
      </div>
    </div>
  );
};

export default Login;
