import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Modal from 'react-modal';
import '../styles/VillaDetail.css';

Modal.setAppElement('#root');

const VillaDetail = () => {
  const { id } = useParams();
  const [villa, setVilla] = useState(null);
  const [spaces, setSpaces] = useState([]);
  const [selectedSpace, setSelectedSpace] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [form, setForm] = useState({
    carType: '',
    carNumber: '',
    contact: '',
    exitTime: '',
    notes: ''
  });
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const fetchVilla = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/villa/${id}`);
        setVilla(response.data);
        setSpaces(response.data.spaces || []);
      } catch (error) {
        console.error('Error fetching villa details:', error);
      }
    };

    const fetchCurrentUser = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/user/current');
        setCurrentUser(response.data);
      } catch (error) {
        console.error('Error fetching current user:', error);
      }
    };

    fetchVilla();
    fetchCurrentUser();
  }, [id]);

  const openModal = async (space) => {
    setSelectedSpace(space);
    if (space.isOccupied && space.userId) {
      try {
        const response = await axios.get(`http://localhost:3001/api/user/${space.userId}`);
        setForm({
          carType: response.data.vehicleName,
          carNumber: response.data.vehicleNumber,
          contact: response.data.phone,
          exitTime: space.exitTime || '',
          notes: space.notes || ''
        });
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    } else {
      setForm({
        carType: '',
        carNumber: '',
        contact: '',
        exitTime: '',
        notes: ''
      });
    }
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setSelectedSpace(null);
    setForm({
      carType: '',
      carNumber: '',
      contact: '',
      exitTime: '',
      notes: ''
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({ ...prevForm, [name]: value }));
  };

  const handleSubmit = async () => {
    if (!selectedSpace || !currentUser) return;

    const updatedSpaces = spaces.map((space) =>
      space.id === selectedSpace.id
        ? {
            ...space,
            ...form,
            isOccupied: true,
            userId: currentUser._id,
          }
        : space
    );
    setSpaces(updatedSpaces);

    try {
      const response = await axios.post(`http://localhost:3001/api/villa/update/${villa._id}`, {
        spaces: updatedSpaces,
      });
      if (response.status === 200) {
        closeModal();
      } else {
        console.error('Error updating parking space:', response);
      }
    } catch (error) {
      console.error('Error updating parking space:', error);
    }
  };

  const handleExit = async () => {
    if (!selectedSpace || !currentUser) return;

    const updatedSpaces = spaces.map((space) =>
      space.id === selectedSpace.id
        ? {
            ...space,
            isOccupied: false,
            userId: null,
            exitTime: '',
            notes: ''
          }
        : space
    );
    setSpaces(updatedSpaces);

    try {
      const response = await axios.post(`http://localhost:3001/api/villa/update/${villa._id}`, {
        spaces: updatedSpaces,
      });
      if (response.status === 200) {
        closeModal();
      } else {
        console.error('Error updating parking space:', response);
      }
    } catch (error) {
      console.error('Error updating parking space:', error);
    }
  };

  if (!villa) return <div>Loading...</div>;

  return (
    <div>
      <h2>{villa.villaName}</h2>
      <p>{villa.address}</p>
      <div style={{ position: 'relative', width: '100%', height: '500px', border: '1px solid black' }}>
        {spaces.map((space) => (
          <div
            key={space.id}
            onClick={() => {
              if (space.type === 'PARKING') {
                openModal(space);
              }
            }}
            className={`space ${space.isOccupied ? 'occupied' : 'available'}`}
            style={{
              left: space.left,
              top: space.top,
              width: space.width,
              height: space.height,
              position: 'absolute',
              backgroundColor: space.isOccupied ? 'red' : 'green',
            }}
          >
            {space.type}
          </div>
        ))}
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Space Details"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            width: '50%',
            height: '50%'
          }
        }}
      >
        <h2>Space Details</h2>
        {selectedSpace && selectedSpace.isOccupied ? (
          currentUser && currentUser._id === selectedSpace.userId ? (
            <div>
              <p>차종: {form.carType}</p>
              <p>차번호: {form.carNumber}</p>
              <p>연락처: {form.contact}</p>
              <p>출차시간: {form.exitTime}</p>
              <p>특이사항: {form.notes}</p>
              <button onClick={handleExit}>출차하기</button>
            </div>
          ) : (
            <div>
              <p>차종: {form.carType}</p>
              <p>차번호: {form.carNumber}</p>
              <p>연락처: {form.contact}</p>
              <p>출차시간: {form.exitTime}</p>
              <p>특이사항: {form.notes}</p>
            </div>
          )
        ) : (
          <div>
            <label>
              출차시간:
              <input type="text" name="exitTime" value={form.exitTime} onChange={handleInputChange} />
            </label>
            <label>
              특이사항:
              <textarea name="notes" value={form.notes} onChange={handleInputChange}></textarea>
            </label>
            <button onClick={handleSubmit}>주차하기</button>
          </div>
        )}
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
};

export default VillaDetail;
