import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import '../styles/ParkingSpaces.css';

const ItemTypes = {
  VILLA: 'villa',
  PARKING: 'parking',
  EXIT: 'exit',
};

const DraggableItem = ({ type, children, left, top }) => {
  const [, drag] = useDrag(() => ({
    type,
    item: { type, left, top },
  }));

  return (
    <div
      ref={drag}
      className="draggable-item"
      style={{ left, top }}
    >
      {children}
    </div>
  );
};

const ParkingArea = ({ spaces, setSpaces }) => {
  const [, drop] = useDrop(() => ({
    accept: [ItemTypes.VILLA, ItemTypes.PARKING, ItemTypes.EXIT],
    drop: (item, monitor) => {
      const delta = monitor.getClientOffset();
      const left = Math.round(delta.x);
      const top = Math.round(delta.y);

      setSpaces((prevSpaces) =>
        prevSpaces.map((space, index) =>
          index === item.index
            ? { ...space, left, top }
            : space
        )
      );
    },
  }));

  return (
    <div ref={drop} className="parking-area">
      {spaces.map((space, index) => (
        <DraggableItem
          key={index}
          index={index}
          type={space.type}
          left={space.left}
          top={space.top}
        >
          {space.type}
        </DraggableItem>
      ))}
    </div>
  );
};

const ParkingSpaces = () => {
  const [spaces, setSpaces] = useState([]);
  const location = useLocation();
  const navigate = useNavigate();
  const { villaName, address } = location.state || {};

  const addSpace = (type) => {
    setSpaces([...spaces, { type, left: 0, top: 0 }]);
  };

  const handleSave = () => {
    // 주차 공간 정보 저장 로직 추가
    console.log('Saved spaces:', spaces);
    navigate('/main');
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div>
        <h2>내 빌라 등록 - 주차 공간 생성</h2>
        <div>
          <label>Villa Name:</label>
          <input type="text" value={villaName} readOnly />
        </div>
        <div>
          <label>Address:</label>
          <input type="text" value={address} readOnly />
        </div>
        <div className="toolbar">
          <button onClick={() => addSpace(ItemTypes.VILLA)}>Add Villa</button>
          <button onClick={() => addSpace(ItemTypes.PARKING)}>Add Parking</button>
          <button onClick={() => addSpace(ItemTypes.EXIT)}>Add Exit</button>
        </div>
        <ParkingArea spaces={spaces} setSpaces={setSpaces} />
        <button type="button" onClick={handleSave}>Save</button>
      </div>
    </DndProvider>
  );
};

export default ParkingSpaces;
