import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Modal from './Modal'; // Modal 컴포넌트가 정의된 파일을 올바르게 import 해주세요.

const VillaDetail = () => {
  const { ivillaId } = useParams();
  const navigate = useNavigate();
  const [villaData, setVillaData] = useState(null);
  const [selectedSpace, setSelectedSpace] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [exitTime, setExitTime] = useState('');
  const [notes, setNotes] = useState('');

  useEffect(() => {
    const fetchCurrentUser = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/user/current', { withCredentials: true });
        setCurrentUser(response.data);
      } catch (error) {
        console.error('Error fetching current user:', error);
      }
    };

    const fetchVilla = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/villa/${id}`);
        if (response.status === 200) {
          setVillaData(response.data);
        }
      } catch (error) {
        console.error('Error fetching villa details:', error);
      }
    };

    fetchCurrentUser();
    fetchVilla();
  }, [id]);

  const openModal = (space) => {
    setSelectedSpace(space);
    setModalOpen(true);
  };

  const closeModal = () => {
    setSelectedSpace(null);
    setModalOpen(false);
  };

  const handleSubmit = async () => {
    if (!selectedSpace || !currentUser) {
      console.log('No space or user selected');
      return;
    }

    try {
      const response = await axios.post(
        `http://localhost:3001/api/villa/${id}/update-space/${selectedSpace._id}`,
        {
          exitTime,
          notes,
          isOccupied: true,
          userId: currentUser.id,
        }
      );
      console.log('Updated space data:', response.data);
      setVillaData((prevVillaData) => ({
        ...prevVillaData,
        spaces: prevVillaData.spaces.map((space) =>
          space._id === selectedSpace._id ? response.data : space
        ),
      }));
      closeModal();
    } catch (error) {
      console.error('Error updating parking space:', error);
    }
  };

  if (!villaData) {
    return (
      <div>
        <h2>등록된 빌라가 없습니다.</h2>
        <button onClick={() => navigate('/main')}>메인 페이지로</button>
      </div>
    );
  }

  return (
    <div>
      <h2>{villaData.villaName}</h2>
      <p>주소: {villaData.address}</p>
      <p>빌라 ID: {villaData.villaId}</p>
      {villaData.spaces.map((space) => (
        <div key={space._id} onClick={() => space.type === 'PARKING' && openModal(space)}>
          {space.type === 'PARKING' ? '주차칸' : space.type}
        </div>
      ))}
      {modalOpen && (
        <Modal onClose={closeModal}>
          <h2>주차 공간 정보</h2>
          <p>차종: {currentUser.vehicleName}</p>
          <p>차번호: {currentUser.vehicleNumber}</p>
          <p>연락처: {currentUser.phone}</p>
          <label>
            출차시간:
            <input
              type="text"
              value={exitTime}
              onChange={(e) => setExitTime(e.target.value)}
            />
          </label>
          <label>
            특이사항:
            <input
              type="text"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
            />
          </label>
          <button onClick={handleSubmit}>주차하기</button>
        </Modal>
      )}
    </div>
  );
};

export default VillaDetail;
