import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Modal from 'react-modal';
import '../styles/VillaDetail.css';

Modal.setAppElement('#root');

const VillaDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [villa, setVilla] = useState(null);
  const [spaces, setSpaces] = useState([]);
  const [selectedSpace, setSelectedSpace] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const fetchVilla = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/villa/${id}`);
        setVilla(response.data);
        setSpaces(response.data.spaces || []);
      } catch (error) {
        console.error('Error fetching villa details:', error);
      }
    };

    const fetchCurrentUser = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/user/current', { withCredentials: true });
        setCurrentUser(response.data);
      } catch (error) {
        console.error('Error fetching current user:', error);
      }
    };

    fetchVilla();
    fetchCurrentUser();
  }, [id]);

  const openModal = async (space) => {
    if (space.type !== 'PARKING') return; // 모달이 열리지 않도록 함
    setSelectedSpace(space);
    if (space.isOccupied) {
      try {
        const userResponse = await axios.get(`http://localhost:3001/api/user/${space.username}`, { withCredentials: true });
        setSelectedSpace({ ...space, ...userResponse.data });
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    }
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setSelectedSpace(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSelectedSpace((prevSpace) => ({ ...prevSpace, [name]: value }));
  };

  const handleSubmit = async () => {
    try {
      await axios.post(`http://localhost:3001/api/villa/${id}/update-space/${selectedSpace._id}`, {
        ...selectedSpace,
        isOccupied: true,
        username: currentUser.username
      });
      setSpaces((prevSpaces) =>
        prevSpaces.map((space) =>
          space._id === selectedSpace._id ? { ...space, ...selectedSpace, isOccupied: true, username: currentUser.username } : space
        )
      );
      closeModal();
    } catch (error) {
      console.error('Error updating parking space:', error);
    }
  };

  const handleExit = async () => {
    try {
      await axios.post(`http://localhost:3001/api/villa/${id}/update-space/${selectedSpace._id}`, {
        ...selectedSpace,
        isOccupied: false,
        username: null,
        vehicleName: null,
        vehicleNumber: null,
        contact: null,
        exitTime: null,
        notes: null
      });
      setSpaces((prevSpaces) =>
        prevSpaces.map((space) =>
          space._id === selectedSpace._id ? { ...space, isOccupied: false, username: null, vehicleName: null, vehicleNumber: null, contact: null, exitTime: null, notes: null } : space
        )
      );
      closeModal();
    } catch (error) {
      console.error('Error updating parking space:', error);
    }
  };

  if (!villa) return <div>Loading...</div>;

  const handleGoToMain = () => {
    navigate('/main');
  };

  return (
    <div>
      <h2>{villa.villaName}</h2>
      <p>{villa.address}</p>
      <p>방 고유번호: {villa._id}</p>
      <img src={villa.qrCodeUrl} alt="QR Code" />
      <button onClick={handleGoToMain}>메인 페이지로</button>
      <div style={{ position: 'relative', width: '100%', height: '500px', border: '1px solid black' }}>
        {spaces.map((space) => (
          <div
            key={space._id}
            onClick={() => openModal(space)}
            className={`space ${space.isOccupied ? 'occupied' : 'available'}`}
            style={{
              left: space.left,
              top: space.top,
              width: space.width,
              height: space.height
            }}
          >
            {space.type}
          </div>
        ))}
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Space Details"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            width: '50%',
            height: '50%'
          }
        }}
      >
        <h2>Space Details</h2>
        {selectedSpace && selectedSpace.isOccupied ? (
          <div>
            <p>차종: {selectedSpace.vehicleName}</p>
            <p>차번호: {selectedSpace.vehicleNumber}</p>
            <p>연락처: {selectedSpace.contact}</p>
            <p>출차시간: {selectedSpace.exitTime}</p>
            <p>특이사항: {selectedSpace.notes}</p>
            {selectedSpace.username === currentUser.username && <button onClick={handleExit}>출차하기</button>}
          </div>
        ) : (
          <div>
            <label>
              출차시간:
              <input type="text" name="exitTime" value={selectedSpace?.exitTime || ''} onChange={handleInputChange} />
            </label>
            <label>
              특이사항:
              <textarea name="notes" value={selectedSpace?.notes || ''} onChange={handleInputChange}></textarea>
            </label>
            <button onClick={handleSubmit}>주차하기</button>
          </div>
        )}
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
};

export default VillaDetail;
