import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const MyVilla = () => {
  const [villas, setVillas] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchVillas = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/villa/user-villas', { withCredentials: true });
        setVillas(response.data);
      } catch (error) {
        console.error('Error fetching villas:', error);
      }
    };

    fetchVillas();
  }, []);

  const handleDeleteVilla = async (villaId) => {
    try {
      const response = await axios.delete(`http://localhost:3001/api/villa/delete/${villaId}`, { withCredentials: true });
      if (response.status === 200) {
        setVillas(villas.filter(villa => villaId !== villaId));
        alert('빌라가 성공적으로 삭제되었습니다.');
      }
    } catch (error) {
      console.error('Error deleting villa:', error);
      alert('빌라 삭제 중 오류가 발생했습니다.');
    }
  };

  const handleGoToMain = () => {
    navigate('/main');
  };

  return (
    <div>
      <h2>내 빌라 목록</h2>
      {villas.length > 0 ? (
        <ul>
          {villas.map(villaId => (
            <li key={villaId}>
              <span>{villa.villaName}</span>
              <button onClick={() => navigate(`/villa/${villaId}`)}>보기</button>
              <button onClick={() => handleDeleteVilla(villaId)}>삭제</button>
            </li>
          ))}
        </ul>
      ) : (
        <div>
          <p>등록된 빌라가 없습니다.</p>
          <button onClick={handleGoToMain}>메인 페이지로</button>
        </div>
      )}
    </div>
  );
};

export default MyVilla;
