import React, { useState } from 'react';
import AddressSearch from './AddressSearch';
import ParkingArea from './ParkingArea';

const RegisterVilla = () => {
  const [villaName, setVillaName] = useState('');
  const [address, setAddress] = useState('');
  const [mode, setMode] = useState(''); // 추가: 모드 상태 ('add' 또는 'delete')

  const handleAddressSelect = (selectedAddress) => {
    setAddress(selectedAddress.address_name);
  };

  const handleSubmit = () => {
    // 제출 로직 추가
    console.log('Villa Name:', villaName);
    console.log('Address:', address);
  };

  return (
    <div>
      <h2>빌라 등록</h2>
      <div>
        <label>빌라 이름</label>
        <input
          type="text"
          value={villaName}
          onChange={(e) => setVillaName(e.target.value)}
          placeholder="빌라 이름을 입력하세요"
        />
      </div>
      <div>
        <label>주소</label>
        <AddressSearch onSelect={handleAddressSelect} />
        {address && <p>선택된 주소: {address}</p>}
      </div>
      <div>
        <button onClick={() => setMode('add')}>추가 모드</button>
        <button onClick={() => setMode('delete')}>삭제 모드</button>
      </div>
      <ParkingArea mode={mode} />
      <button onClick={handleSubmit}>등록</button>
    </div>
  );
};

export default RegisterVilla;
