import React, { useRef } from 'react';
import { useDrag, useDragLayer } from 'react-dnd';
import { ResizableBox } from 'react-resizable';
import '../styles/DraggableItem.css';

const DraggableResizableItem = ({ id, type, left, top, width = 50, height = 50, onResize, onMove }) => {
  const [{ isDragging }, drag, preview] = useDrag(() => ({
    type,
    item: { id, type, left, top, width, height },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  const handleResize = (e, { size }) => {
    onResize(id, size.width, size.height);
  };

  const handleStop = (e, data) => {
    onMove(id, data.x, data.y);
  };

  return (
    <ResizableBox
      width={width}
      height={height}
      minConstraints={[20, 20]}
      maxConstraints={[200, 200]}
      onResize={handleResize}
      className="draggable-item"
      resizeHandles={['se']}
      style={{ position: 'absolute', left, top }}
    >
      <div ref={preview} style={{ width: '100%', height: '100%' }}>
        <div ref={drag} style={{ width: '100%', height: '100%' }}>
          {type}
        </div>
      </div>
    </ResizableBox>
  );
};

export default DraggableResizableItem;
