import React from 'react';
import { useDrop } from 'react-dnd';
import DraggableResizableItem from './DraggableResizableItem';
import { ItemTypes } from './ItemTypes';
import './ParkingArea.css';

const ParkingArea = ({ spaces, setSpaces, moveSpace, resizeSpace, deleteSpace }) => {
  const [, drop] = useDrop({
    accept: [ItemTypes.VILLA, ItemTypes.PARKING, ItemTypes.EXIT, ItemTypes.WALL],
    drop: (item, monitor) => {
      const delta = monitor.getClientOffset();
      if (!delta) return;
      const { x, y } = delta;
      const newItem = { ...item, left: x, top: y };
      setSpaces(prevSpaces => [...prevSpaces, newItem]);
    },
  });

  return (
    <div ref={drop} className="parking-area">
      {spaces.map((space) => (
        <DraggableResizableItem
          key={space.id}
          id={space.id}
          type={space.type}
          left={space.left}
          top={space.top}
          width={space.width}
          height={space.height}
          onMove={moveSpace}
          onResize={resizeSpace}
          onDelete={deleteSpace}
        >
          {space.type}
        </DraggableResizableItem>
      ))}
    </div>
  );
};

export default ParkingArea;
