import React, { useState, useEffect } from 'react';

const Modal = ({ isOpen, onClose, isOccupied, onSave, children }) => {
  const [departureTime, setDepartureTime] = useState('');
  const [notes, setNotes] = useState('');

  const handleSave = () => {
    const details = {
      departureTime,
      notes,
    };
    onSave(details);
    onClose();
  };

  useEffect(() => {
    if (isOccupied) {
      setDepartureTime('');
      setNotes('');
    }
  }, [isOccupied]);

  if (!isOpen) return null;

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        {children || (
          <>
            <h2>{isOccupied ? '주차 정보' : '주차하기'}</h2>
            {!isOccupied && (
              <>
                <input
                  type="text"
                  placeholder="출차시간"
                  value={departureTime}
                  onChange={(e) => setDepartureTime(e.target.value)}
                />
                <input
                  type="text"
                  placeholder="특이사항"
                  value={notes}
                  onChange={(e) => setNotes(e.target.value)}
                />
                <button onClick={handleSave}>주차하기</button>
              </>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default Modal;
