import React from 'react';
import { useDrag, useDrop } from 'react-dnd';
import { Resizable } from 're-resizable';
import { ItemTypes } from './ItemTypes';
import '../styles/DraggableResizableItem.css';

const DraggableResizableItem = ({ id, type, left, top, width, height, onResize, onMove, onDelete, children }) => {
  const [, drag] = useDrag({
    type,
    item: { id, left, top, width, height, type },
  });

  const [, drop] = useDrop({
    accept: ItemTypes.VILLA,
    hover: (item, monitor) => {
      const delta = monitor.getDifferenceFromInitialOffset();
      const newLeft = Math.round(left + delta.x);
      const newTop = Math.round(top + delta.y);
      onMove(id, newLeft, newTop);
    },
  });

  return (
    <div
      ref={(node) => drag(drop(node))}
      style={{ position: 'absolute', left, top }}
      className="draggable-resizable-item"
    >
      <Resizable
        size={{ width, height }}
        onResizeStop={(e, direction, ref, d) => {
          onResize(id, width + d.width, height + d.height);
        }}
        style={{ border: '1px solid black', padding: '10px', backgroundColor: 'white' }}
      >
        {children}
        <button onClick={() => onDelete(id)}>Delete</button>
      </Resizable>
    </div>
  );
};

export default DraggableResizableItem;
