import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import ParkingArea from './ParkingArea';
import { ItemTypes } from './ItemTypes'; // ItemTypes 가져오기
import axios from 'axios';
import '../styles/ParkingSpaces.css';

const ParkingSpaces = () => {
  const [spaces, setSpaces] = useState([]);
  const [userId, setUserId] = useState(null);
  const location = useLocation();
  const navigate = useNavigate();
  const { villaName, address } = location.state || {};

  useEffect(() => {
    const fetchCurrentUser = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/user/current', { withCredentials: true });
        setUserId(response.data.id); // Assuming the response contains the user's ID in `data.id`
      } catch (error) {
        console.error('Error fetching current user:', error);
      }
    };

    fetchCurrentUser();
  }, []);

  const addSpace = (type) => {
    const id = spaces.length + 1;
    setSpaces([...spaces, { id, type, left: 0, top: 0, width: 50, height: 50 }]);
  };

  const moveSpace = (id, left, top) => {
    setSpaces((prevSpaces) =>
      prevSpaces.map((space) =>
        space.id === id ? { ...space, left, top } : space
      )
    );
  };

  const resizeSpace = (id, width, height) => {
    setSpaces((prevSpaces) =>
      prevSpaces.map((space) =>
        space.id === id ? { ...space, width, height } : space
      )
    );
  };

  const deleteSpace = (id) => {
    setSpaces((prevSpaces) =>
      prevSpaces.filter((space) => space.id !== id)
    );
  };

  const handleSave = async () => {
    try {
      const response = await axios.post('http://localhost:3001/api/villa/save', {
        villaName,
        address,
        spaces,
        userId,
      });
      if (response.status === 201) {
        const { villaId, qrCode } = response.data;
        navigate('/villa-qrcode', { state: { villaId, qrCode } });
      }
    } catch (error) {
      console.error('Error saving villa:', error);
    }
  };

  return (
    <div>
      <h2>내 빌라 등록 - 주차 공간 생성</h2>
      <div>
        <label> 빌라 이름 : </label>
        <input type="text" value={villaName} readOnly />
      </div>
      <div>
        <label> 주소 : </label>
        <input type="text" value={address} readOnly />
      </div>
      <div className="toolbar">
        <button onClick={() => addSpace(ItemTypes.VILLA)}> 빌라 </button>
        <button onClick={() => addSpace(ItemTypes.PARKING)}> 주차칸 </button>
        <button onClick={() => addSpace(ItemTypes.EXIT)}> 출구 </button>
        <button onClick={() => addSpace(ItemTypes.WALL)}> 벽 </button>
      </div>
      <ParkingArea spaces={spaces} setSpaces={setSpaces} moveSpace={moveSpace} resizeSpace={resizeSpace} deleteSpace={deleteSpace} />
      <button type="button" onClick={handleSave}> 저장 </button>
    </div>
  );
};

export default ParkingSpaces;
