import React, { useState } from 'react';
import axios from 'axios';
import '../styles/MainPage.css';

const MainPage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [villaOptions, setVillaOptions] = useState([]);
  const [message, setMessage] = useState('');

  const handleSearchChange = async (event) => {
    const address = event.target.value;
    setSearchQuery(address);

    try {
      const response = await axios.get(`http://localhost:3001/api/villa/search?address=${address}`, { withCredentials: true });
      setVillaOptions(response.data);
    } catch (error) {
      console.error('Error fetching villas:', error);
      setMessage('Error fetching villas');
    }
  };

  const handleVillaSelect = (villaName) => {
    // 빌라 선택 시 입장 기능 구현
    console.log(`Selected villa: ${villaName}`);
    // navigate 또는 axios를 통해 빌라 입장 로직 추가
  };

  const handleLogout = async () => {
    try {
      const response = await axios.post('http://localhost:3001/logout', {}, { withCredentials: true });
      if (response && response.data) {
        localStorage.removeItem('token');
        window.location.href = '/login';
      }
    } catch (error) {
      console.error('Error logging out', error);
    }
  };

  return (
    <div className="main-container">
      <h2>Main Page</h2>
      <div className="search-container">
        <input
          type="text"
          placeholder="빌라 주소 검색"
          value={searchQuery}
          onChange={handleSearchChange}
          className="search-input"
        />
        {villaOptions.length > 0 && (
          <ul className="villa-dropdown">
            {villaOptions.map((villa) => (
              <li key={villa._id} onClick={() => handleVillaSelect(villa.name)}>
                {villa.name}
              </li>
            ))}
          </ul>
        )}
      </div>
      <div className="button-group">
        <button onClick={() => handleVillaSelect(searchQuery)} className="btn main-btn">내 빌라</button>
        <button onClick={() => console.log('Enter Villa')} className="btn main-btn">빌라 입장</button>
        <button onClick={() => console.log('Register Villa')} className="btn main-btn">내 빌라 등록</button>
        <button onClick={handleLogout} className="btn logout-btn">Logout</button>
      </div>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default MainPage;
