import React from 'react';
import { useDrop } from 'react-dnd';
import DraggableResizableItem from './DraggableResizableItem';
import '../styles/ParkingArea.css';

const ParkingArea = ({ spaces, setSpaces }) => {
  const [, drop] = useDrop({
    accept: ['villa', 'parking-space', 'exit', 'wall'],
    drop: (item, monitor) => {
      const delta = monitor.getClientOffset();
      if (!delta) return; // Ensure delta is not null
      const { x, y } = delta;
      const newItem = { ...item, left: x, top: y };
      setSpaces(prevSpaces => [...prevSpaces, newItem]);
    },
    hover: () => {
      // 호버 이벤트를 비워둠으로써 드롭 후에 호출되지 않도록 합니다.
    }
  });

  const handleDelete = (id) => {
    setSpaces(prevSpaces => prevSpaces.filter(space => space.id !== id));
  };

  return (
    <div ref={drop} className="parking-area">
      {spaces.map((space, index) => (
        <DraggableResizableItem
          key={index}
          id={space.id}
          type={space.type}
          left={space.left}
          top={space.top}
          width={space.width}
          height={space.height}
          onDelete={handleDelete}
        >
          {space.type}
        </DraggableResizableItem>
      ))}
    </div>
  );
};

export default ParkingArea;
