import React, { useState, useEffect } from 'react';
import axios from 'axios';

const VillaListPage = () => {
  const [villas, setVillas] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchVillas = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/villa/all');
        setVillas(response.data);
      } catch (error) {
        console.error('Error fetching villas:', error);
      }
    };

    fetchVillas();
  }, []);

  const handleDeleteResident = async (residentId) => {
    try {
      await axios.delete(`http://localhost:3001/api/villa/resident/${residentId}`);
      setVillas((prevVillas) => prevVillas.map((villa) => ({
        ...villa,
        residents: villa.residents.filter((resident) => resident._id !== residentId)
      })));
    } catch (error) {
      console.error('Error deleting resident:', error);
      setMessage('Error deleting resident');
    }
  };

  return (
    <div>
      <h2>모든 빌라 목록</h2>
      {message && <p>{message}</p>}
      {villas.map((villa) => (
        <div key={villa._id}>
          <h3>{villa.villaName}</h3>
          <p>{villa.address}</p>
          <h4>거주자 목록:</h4>
          <ul>
            {villa.residents.map((resident) => (
              <li key={resident._id}>
                {resident.username}
                <button onClick={() => handleDeleteResident(resident._id)}>삭제</button>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default VillaListPage;
