import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Modal from 'react-modal';
import '../styles/VillaDetail.css';

Modal.setAppElement('#root');

const VillaDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [villa, setVilla] = useState(null);
  const [spaces, setSpaces] = useState([]);
  const [selectedSpace, setSelectedSpace] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [form, setForm] = useState({
    exitTime: '',
    notes: ''
  });
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchVilla = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/villa/${id}`);
        setVilla(response.data);
        setSpaces(response.data.spaces || []);
      } catch (error) {
        console.error('Error fetching villa details:', error);
      }
    };

    fetchVilla();
  }, [id]);

  const openModal = async (space) => {
    setSelectedSpace(space);
    setModalIsOpen(true);
    if (space.isOccupied) {
      try {
        const response = await axios.get(`http://localhost:3001/api/user/${space.userId}`, { withCredentials: true });
        const userData = response.data;
        setUser(userData);
        setForm({
          exitTime: space.exitTime || '',
          notes: space.notes || ''
        });
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    } else {
      setForm({
        exitTime: '',
        notes: ''
      });
      setUser(null);
    }
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setSelectedSpace(null);
    setForm({
      exitTime: '',
      notes: ''
    });
    setUser(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({ ...prevForm, [name]: value }));
  };

  const handleSubmit = async () => {
    if (!selectedSpace) return;

    const updatedSpace = {
      ...selectedSpace,
      ...form,
      isOccupied: true,
      userId: user ? user._id : selectedSpace.userId,
      vehicleName: user ? user.vehicleName : '',
      vehicleNumber: user ? user.vehicleNumber : '',
      contact: user ? user.phone : ''
    };

    try {
      await axios.post(`http://localhost:3001/api/villa/${id}/update-space/${selectedSpace._id}`, updatedSpace, { withCredentials: true });
      setSpaces((prevSpaces) => prevSpaces.map(space => space._id === updatedSpace._id ? updatedSpace : space));
      closeModal();
    } catch (error) {
      console.error('Error updating parking space:', error);
    }
  };

  const handleExit = async () => {
    if (!selectedSpace) return;

    const updatedSpace = {
      ...selectedSpace,
      isOccupied: false,
      userId: null,
      vehicleName: '',
      vehicleNumber: '',
      contact: '',
      exitTime: null,
      notes: ''
    };

    try {
      await axios.post(`http://localhost:3001/api/villa/${id}/update-space/${selectedSpace._id}`, updatedSpace, { withCredentials: true });
      setSpaces((prevSpaces) => prevSpaces.map(space => space._id === updatedSpace._id ? updatedSpace : space));
      closeModal();
    } catch (error) {
      console.error('Error updating parking space:', error);
    }
  };

  const handleGoToMain = () => {
    navigate('/main');
  };

  if (!villa) return <div>Loading...</div>;

  return (
    <div>
      <h2>{villa.villaName}</h2>
      <p>{villa.address}</p>
      <p>고유번호: {villa._id}</p>
      <img src={villa.qrCodeUrl} alt="QR Code" />
      <div style={{ position: 'relative', width: '100%', height: '500px', border: '1px solid black' }}>
        {spaces.map((space) => (
          <div
            key={space._id}
            onClick={() => openModal(space)}
            className={`space ${space.isOccupied ? 'occupied' : 'available'}`}
            style={{
              left: space.left,
              top: space.top,
              width: space.width,
              height: space.height
            }}
          >
            {space.type}
          </div>
        ))}
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Space Details"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            width: '50%',
            height: '50%'
          }
        }}
      >
        <h2>Space Details</h2>
        {selectedSpace && selectedSpace.isOccupied ? (
          <div>
            <p>차종: {user ? user.vehicleName : ''}</p>
            <p>차번호: {user ? user.vehicleNumber : ''}</p>
            <p>연락처: {user ? user.phone : ''}</p>
            <p>출차시간: {selectedSpace.exitTime}</p>
            <p>특이사항: {selectedSpace.notes}</p>
            <button onClick={handleExit}>출차하기</button>
          </div>
        ) : (
          <div>
            <label>
              출차시간:
              <input type="text" name="exitTime" value={form.exitTime} onChange={handleInputChange} />
            </label>
            <label>
              특이사항:
              <textarea name="notes" value={form.notes} onChange={handleInputChange}></textarea>
            </label>
            <button onClick={handleSubmit}>주차하기</button>
          </div>
        )}
        <button onClick={closeModal}>Close</button>
      </Modal>
      <button onClick={handleGoToMain}>메인 페이지로</button>
    </div>
  );
};

export default VillaDetail;
