import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Modal from 'react-modal';

const VillaDetail = () => {
  const { id } = useParams();
  const [villa, setVilla] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedSpace, setSelectedSpace] = useState(null);
  const [exitTime, setExitTime] = useState('');
  const [notes, setNotes] = useState('');

  useEffect(() => {
    const fetchVilla = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/villa/${id}`);
        setVilla(response.data);
      } catch (error) {
        console.error('Error fetching villa details:', error);
      }
    };

    fetchVilla();
  }, [id]);

  const openModal = (space) => {
    setSelectedSpace(space);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setSelectedSpace(null);
    setExitTime('');
    setNotes('');
  };

  const handleParkingClick = (space) => {
    if (space.type === 'P') {
      openModal(space);
    }
  };

  const handleSaveParking = async () => {
    try {
      const updatedSpaces = villa.spaces.map((space) =>
        space.id === selectedSpace.id
          ? { ...space, exitTime, notes, occupied: true } // Update the space with new data
          : space
      );
      await axios.put(`http://localhost:3001/api/villa/${id}`, { ...villa, spaces: updatedSpaces });
      setVilla({ ...villa, spaces: updatedSpaces });
      closeModal();
    } catch (error) {
      console.error('Error saving parking data:', error);
    }
  };

  if (!villa) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{villa.villaName}</h2>
      <p>{villa.address}</p>
      <div className="parking-area">
        {villa.spaces.map((space) => (
          <div
            key={space.id}
            className={`space ${space.type}`}
            style={{
              left: space.left,
              top: space.top,
              width: space.width,
              height: space.height,
              position: 'absolute',
            }}
            onClick={() => handleParkingClick(space)}
          >
            {space.type}
          </div>
        ))}
      </div>
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} contentLabel="Parking Space Details">
        <h2>Parking Space Details</h2>
        {selectedSpace && selectedSpace.occupied ? (
          <div>
            <p>차종: {selectedSpace.vehicleType}</p>
            <p>차번호: {selectedSpace.vehicleNumber}</p>
            <p>연락처: {selectedSpace.contact}</p>
            <p>출차시간: {selectedSpace.exitTime}</p>
            <p>특이사항: {selectedSpace.notes}</p>
          </div>
        ) : (
          <div>
            <label>
              출차시간:
              <input type="datetime-local" value={exitTime} onChange={(e) => setExitTime(e.target.value)} />
            </label>
            <label>
              특이사항:
              <textarea value={notes} onChange={(e) => setNotes(e.target.value)} />
            </label>
            <button onClick={handleSaveParking}>주차하기</button>
          </div>
        )}
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
};

export default VillaDetail;
