import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const checkUsernameAvailability = async () => {
    try {
      const response = await axios.post('http://localhost:3001/api/user/check-username', { username }, { withCredentials: true });
      if (response.data.available) {
        setMessage('Username is available');
      } else {
        setMessage('Username is already taken');
      }
    } catch (error) {
      setMessage('There was an error checking the username!');
      console.error(error);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/register', { username, password }, { withCredentials: true });
      setMessage(response.data.message);
    } catch (error) {
      setMessage('Error registering user');
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            onBlur={checkUsernameAvailability}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Register</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Register;
