import React from 'react';
import { useDrop } from 'react-dnd';
import DraggableResizableItem from './DraggableResizableItem';
import { ItemTypes } from './ItemTypes';

const ParkingArea = ({ spaces, setSpaces, moveSpace, resizeSpace, deleteSpace }) => {
  const [, drop] = useDrop({
    accept: Object.values(ItemTypes),
    drop: (item, monitor) => {
      const delta = monitor.getDifferenceFromInitialOffset();
      const left = Math.round(item.left + delta.x);
      const top = Math.round(item.top + delta.y);
      moveSpace(item.id, left, top);
      return undefined;
    },
  });

  return (
    <div ref={drop} style={{ position: 'relative', width: '100%', height: '100%' }}>
      {spaces.map((space) => (
        <DraggableResizableItem
          key={space.id}
          id={space.id}
          type={space.type}
          left={space.left}
          top={space.top}
          width={space.width}
          height={space.height}
          isOccupied={space.isOccupied}
          onMove={moveSpace}
          onResize={resizeSpace}
          onDelete={deleteSpace}
        />
      ))}
    </div>
  );
};

export default ParkingArea;
