import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Modal from 'react-modal';

Modal.setAppElement('#root');

const VillaDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [villa, setVilla] = useState(null);
  const [spaces, setSpaces] = useState([]);
  const [selectedSpace, setSelectedSpace] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [form, setForm] = useState({
    exitTime: '',
    notes: '',
  });
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const fetchCurrentUser = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/user/current', { withCredentials: true });
        setCurrentUser(response.data);
      } catch (error) {
        console.error('Error fetching current user:', error);
      }
    };

    fetchCurrentUser();
  }, []);

  useEffect(() => {
    const fetchVilla = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/villa/${id}`);
        setVilla(response.data);
        setSpaces(response.data.spaces || []);
      } catch (error) {
        console.error('Error fetching villa details:', error);
      }
    };

    fetchVilla();
  }, [id]);

  const openModal = (space) => {
    setSelectedSpace(space);
    setModalIsOpen(true);
    if (space.isOccupied) {
      setForm({
        exitTime: space.exitTime || '',
        notes: space.notes || ''
      });
    }
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setSelectedSpace(null);
    setForm({
      exitTime: '',
      notes: ''
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({ ...prevForm, [name]: value }));
  };

  const handleSubmit = async () => {
    if (!selectedSpace || !currentUser) {
      console.error('No space or user selected');
      return;
    }

    try {
      const updatedSpace = {
        ...selectedSpace,
        exitTime: form.exitTime,
        notes: form.notes,
        isOccupied: true,
        userId: currentUser.id,
        vehicleName: currentUser.vehicleName,
        vehicleNumber: currentUser.vehicleNumber,
        contact: currentUser.phone
      };

      await axios.post(`http://localhost:3001/api/villa/${id}/update-space/${selectedSpace._id}`, updatedSpace);

      setSpaces((prevSpaces) =>
        prevSpaces.map((space) =>
          space._id === selectedSpace._id ? updatedSpace : space
        )
      );

      closeModal();
    } catch (error) {
      console.error('Error updating parking space:', error);
    }
  };

  const handleExit = async () => {
    if (!selectedSpace || !currentUser) {
      console.error('No space or user selected');
      return;
    }

    try {
      const updatedSpace = {
        ...selectedSpace,
        isOccupied: false,
        exitTime: '',
        notes: '',
        userId: null,
        vehicleName: '',
        vehicleNumber: '',
        contact: ''
      };

      await axios.post(`http://localhost:3001/api/villa/${id}/update-space/${selectedSpace._id}`, updatedSpace);

      setSpaces((prevSpaces) =>
        prevSpaces.map((space) =>
          space._id === selectedSpace._id ? updatedSpace : space
        )
      );

      closeModal();
    } catch (error) {
      console.error('Error updating parking space:', error);
    }
  };

  if (!villa) return <div>Loading...</div>;

  return (
    <div>
      <h2>{villa.villaName}</h2>
      <p>{villa.address}</p>
      <div style={{ position: 'relative', width: '100%', height: '500px', border: '1px solid black' }}>
        {spaces.map((space) => (
          <div
            key={space._id}
            onClick={() => {
              if (space.type === 'PARKING') {
                openModal(space);
              }
            }}
            className={`space ${space.isOccupied ? 'occupied' : 'available'}`}
            style={{
              left: space.left,
              top: space.top,
              width: space.width,
              height: space.height
            }}
          >
            {space.type}
          </div>
        ))}
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Space Details"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)'
          }
        }}
      >
        {selectedSpace && selectedSpace.isOccupied && (
          <div>
            <h2>차종: {selectedSpace.vehicleName}</h2>
            <p>차번호: {selectedSpace.vehicleNumber}</p>
            <p>연락처: {selectedSpace.contact}</p>
          </div>
        )}
        <div>
          <label>출차시간</label>
          <input
            type="text"
            name="exitTime"
            value={form.exitTime}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>특이사항</label>
          <input
            type="text"
            name="notes"
            value={form.notes}
            onChange={handleInputChange}
          />
        </div>
        <button onClick={handleSubmit}>주차하기</button>
        {selectedSpace && selectedSpace.isOccupied && (
          <button onClick={handleExit}>출차하기</button>
        )}
        <button onClick={closeModal}>닫기</button>
      </Modal>
    </div>
  );
};

export default Villa
