import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const VillaResidents = () => {
  const { villaId } = useParams();
  const [residents, setResidents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchResidents = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/villa/${villaId}/residents`, { withCredentials: true });
        setResidents(response.data);
      } catch (error) {
        console.error('Error fetching residents:', error);
      }
    };

    fetchResidents();
  }, [villaId]);

  const handleDeleteUser = async (userId) => {
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/api/user/delete/${userId}`);
      setUsers((prevUsers) => prevUsers.filter((user) => user._id !== userId));
      setMessage('User deleted successfully');
    } catch (error) {
      console.error('Error deleting user:', error);
      setMessage('Error deleting user');
    }
  };

  const handleViewUser = (userId) => {
    navigate(`/user-info/${userId}`);
  };

  return (
    <div>
      <ul>
        {residents.map((resident) => (
          <li key={resident._id}>{resident.username}
          <button className="btn view-btn userbtn" onClick={() => handleViewUser(user._id)}>회원정보</button>
          <button className="btn delete-btn userbtn" onClick={() => handleDeleteUser(user._id)}>삭제</button>
          </li>
        ))}
      </ul>
      <button onClick={() => navigate(-1)}>Back</button>
    </div>
  );
};

export default VillaResidents;
