import React, { useRef } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import { Resizable } from 're-resizable';
import { ItemTypes } from './ItemTypes';

const DraggableResizableItem = ({ id, type, left, top, width, height, moveSpace, resizeSpace, deleteSpace }) => {
  const ref = useRef(null);

  const [, drop] = useDrop({
    accept: ItemTypes.SPACE,
    hover(item, monitor) {
      if (!ref.current) {
        return;
      }
      const dragIndex = item.id;
      const hoverIndex = id;

      // Don't replace items with themselves
      if (dragIndex === hoverIndex) {
        return;
      }

      const hoverBoundingRect = ref.current?.getBoundingClientRect();
      const hoverMiddleY = (hoverBoundingRect.bottom - hoverBoundingRect.top) / 2;
      const clientOffset = monitor.getClientOffset();
      const hoverClientY = clientOffset.y - hoverBoundingRect.top;

      if (dragIndex < hoverIndex && hoverClientY < hoverMiddleY) {
        return;
      }

      if (dragIndex > hoverIndex && hoverClientY > hoverMiddleY) {
        return;
      }

      moveSpace(dragIndex, hoverIndex);
      item.index = hoverIndex;
    },
  });

  const [{ isDragging }, drag] = useDrag({
    type: ItemTypes.SPACE,
    item: { id, left, top },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  drag(drop(ref));

  const handleResizeStop = (e, direction, ref, d) => {
    resizeSpace(id, ref.style.width, ref.style.height);
  };

  const handleDelete = () => {
    deleteSpace(id);
  };

  return (
    <div
      ref={ref}
      style={{
        position: 'absolute',
        left,
        top,
        opacity: isDragging ? 0.5 : 1,
        cursor: 'move',
      }}
    >
      <Resizable
        size={{ width, height }}
        onResizeStop={handleResizeStop}
        style={{ border: '1px solid black' }}
      >
        <div style={{ width: '100%', height: '100%', position: 'relative' }}>
          {type}
          <button onClick={handleDelete} style={{ position: 'absolute', top: 0, right: 0 }}>x</button>
        </div>
      </Resizable>
    </div>
  );
};

export default DraggableResizableItem;
