import React from 'react';
import { useDrag, useDrop } from 'react-dnd';
import { Resizable } from 're-resizable';
import '../styles/DraggableResizableItem.css';

const DraggableResizableItem = ({ id, type, left, top, width, height, onDelete }) => {
  const [{ isDragging }, drag] = useDrag({
    type,
    item: { id, type, left, top, width, height },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  const [, drop] = useDrop({
    accept: type,
    hover: (item, monitor) => {
      if (!monitor.canDrop()) return;
      // Ensure this hover logic does not conflict with drop
    },
  });

  return (
    <div
      ref={(node) => drag(drop(node))}
      style={{ left, top, width, height, position: 'absolute', opacity: isDragging ? 0.5 : 1 }}
      className="draggable-resizable-item"
    >
      <Resizable
        size={{ width, height }}
        style={{ position: 'relative' }}
        onResizeStop={(e, direction, ref, d) => {
          const newWidth = width + d.width;
          const newHeight = height + d.height;
          // Update the dimensions
        }}
      >
        <div className="content">
          {type}
          <button onClick={() => onDelete(id)}>Delete</button>
        </div>
      </Resizable>
    </div>
  );
};

export default DraggableResizableItem;
