import React, { useState } from 'react';
import Modal from './Modal';
import '../styles/ParkingSpaces.css';

const VillaDetail = ({ spaces, updateSpaces }) => {
  const [selectedSpace, setSelectedSpace] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleSpaceClick = (space) => {
    setSelectedSpace(space);
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
    setSelectedSpace(null);
  };

  const handleSaveDetails = (details) => {
    updateSpaces((prevSpaces) =>
      prevSpaces.map((space) =>
        space.id === selectedSpace.id ? { ...space, ...details, isOccupied: true } : space
      )
    );
  };

  return (
    <div>
      <h2>Villa Details</h2>
      <div className="parking-area">
        {spaces.map((space) => (
          <div
            key={space.id}
            className={`parking-space ${space.isOccupied ? 'occupied' : 'vacant'}`}
            onClick={() => handleSpaceClick(space)}
          >
            {space.type}
          </div>
        ))}
      </div>
      {selectedSpace && (
        <Modal
          isOpen={isModalOpen}
          onClose={handleModalClose}
          isOccupied={selectedSpace.isOccupied}
          onSave={handleSaveDetails}
        />
      )}
    </div>
  );
};

export default VillaDetail;
